select c.id,c.idMedico,c.idPaciente,c.descricao,c.dataa,m.id as idDr,m.nome, m.crm,e.id as idE,e.nome as nomeE, p.id as idP, p.nome as nomeP, s.id as idSangue, s.tipo as tipoSangue, p.cpf, p.sexo, p.idUser as idUserP  from consulta c, medico m, especialidade e, paciente p, sangue s where c.idMedico = m.id and c.idPaciente = p.id and m.idEspecialidade = e.id and p.TipoSanguineo = s.id order by c.dataa desc